$.validator.setDefaults({
    submitHandler: function submitHandler() {
        alert('submitted!');
    }
});
$('#createAccountForm').validate({
    rules: {
        username: {required: true, minlength: 3, maxlength: 15},
        password: {required: true, minlength: 5},
        confirm_password: {required: true, minlength: 5, equalTo: '#password'},
        email: {required: true, email: true},
        agree: 'required'
    },
    messages: {
        username: {
            required: 'Lütfen bir kullanıcı adı giriniz',
            minlength: 'Kullanıcı adınız çok kısa; en az 3 karakter olmalı',
            maxlength: 'Kullanıcı adınız çok uzun; 15 karakterden daha fazla olmamalı'
        },
        password: {
            required: 'Please provide a password',
            minlength: 'Your password must be at least 5 characters long'
        },
        confirm_password: {
            required: 'Please provide a password',
            minlength: 'Your password must be at least 5 characters long',
            equalTo: 'Please enter the same password as above'
        },
        email: 'Please enter a valid email address',
        agree: 'Please accept our policy'
    },
    errorElement: 'em',
    errorPlacement: function errorPlacement(error, element) {
        error.addClass('invalid-feedback');
        if (element.prop('type') === 'checkbox') {
            error.insertAfter(element.parent('label'));
        } else {
            error.insertAfter(element);
        }
    },
    highlight: function highlight(element) {
        $(element).addClass('is-invalid').removeClass('is-valid');
    },
    unhighlight: function unhighlight(element) {
        $(element).addClass('is-valid').removeClass('is-invalid');
    }
});